USE ROLE ACCOUNTADMIN;
CREATE RESOURCE MONITOR DEV_ACCOUNT_RM CREDIT_QUOTA = 200 TRIGGERS ON 50 PERCENT DO NOTIFY ON 75 PERCENT DO NOTIFY ON 90 PERCENT DO NOTIFY;
USE ROLE SECURITYADMIN;
CREATE NETWORK POLICY IF NOT EXISTS ACCOUNT_NETWORK_POLICY ALLOWED_IP_LIST = ('0.0.0.0/0') BLOCKED_IP_LIST = () COMMENT = 'The default network policy';
USE ROLE SECURITYADMIN;
CREATE USER IF NOT EXISTS SVC_PRD_WF_AP_BILL_CODING_USER DISPLAY_NAME = null, FIRST_NAME = null, MIDDLE_NAME = null, LAST_NAME = null, EMAIL = null, MUST_CHANGE_PASSWORD = false, DISABLED = false, PASSWORD = null, DEFAULT_ROLE = null, DEFAULT_WAREHOUSE = null, DEFAULT_NAMESPACE = null, DEFAULT_SECONDARY_ROLES = null, RSA_PUBLIC_KEY = '', RSA_PUBLIC_KEY_2 = null, COMMENT = null, DAYS_TO_EXPIRY = 0;
USE ROLE ACCOUNTADMIN;
ALTER ACCOUNT SET RESOURCE_MONITOR = DEV_ACCOUNT_RM;
USE ROLE ACCOUNTADMIN;
ALTER ACCOUNT SET ABORT_DETACHED_QUERY = true ALLOW_CLIENT_MFA_CACHING = true ALLOW_ID_TOKEN = true CLIENT_ENCRYPTION_KEY_SIZE = 256 PREVENT_UNLOAD_TO_INLINE_URL = true REQUIRE_STORAGE_INTEGRATION_FOR_STAGE_CREATION = true REQUIRE_STORAGE_INTEGRATION_FOR_STAGE_OPERATION = true STATEMENT_QUEUED_TIMEOUT_IN_SECONDS = 7200 STATEMENT_TIMEOUT_IN_SECONDS = 21600 TIMEZONE = 'UTC' TRANSACTION_ABORT_ON_ERROR = true;
